var button=document.querySelector(".contact-tel + .button"),popup=document.querySelector(".feedback-modal"),close=popup.querySelector(".close-button"),form=popup.querySelector("form"),login=popup.querySelector("[name=login]"),mail=popup.querySelector("[name=email]"),feedback=popup.querySelector("[name=letter]");button.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){login.value&&mail.value&&feedback.value||e.preventDefault()}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});